const tabButtons=document.querySelectorAll(".section-tabs__btn"),tabItems=document.querySelectorAll(".section-tabs__content");tabButtons.forEach(((o,e)=>{o.addEventListener("click",(()=>{tabButtons.forEach((o=>o.classList.remove("js-active"))),o.classList.add("js-active"),tabItems.forEach((o=>o.classList.remove("js-active"))),tabItems[e].classList.add("js-active")}))}));const scrollTopBtn=document.querySelector(".scroll-top");scrollTopBtn.addEventListener("click",(()=>{window.scrollTo({top:0,left:0,behavior:"smooth"})}));const openModalBtn=document.querySelector(".js-iysModalBtn"),closeModalBtn=document.querySelector(".iys-modal__close"),body=document.querySelector("body"),iysModal=document.querySelector(".iys-modal");function openModal(){iysModal.classList.add("js-open-modal"),body.classList.add("js-overflowHidden")}function closeModal(){iysModal.classList.remove("js-open-modal"),body.classList.remove("js-overflowHidden")}openModalBtn.addEventListener("click",(()=>openModal())),closeModalBtn.addEventListener("click",(()=>closeModal()));